function classNames(...xs) {
  return xs.filter(Boolean).join(" ");
}

function useIsomorphicLayoutEffect(effect, deps) {
  const isClient = typeof window !== "undefined";
  const useEffectImpl = isClient ? useEffect : () => {};
  return useEffectImpl(effect, deps);
}

function Slider({ images = [], title }) {
  const scrollerRef = useRef(null);
  const [index, setIndex] = useState(0);
  const id = useId();

  const slides = images.length > 0 ? images : [null, null, null];

  const scrollTo = (i) => {
    const el = scrollerRef.current;
    if (!el) return;
    const child = el.children[i];
    if (child) child.scrollIntoView({ behavior: "smooth", inline: "center", block: "nearest" });
  };

  useIsomorphicLayoutEffect(() => {
    const el = scrollerRef.current;
    if (!el) return;
    const onScroll = () => {
      const children = Array.from(el.children);
      const centers = children.map((c) => Math.abs(c.getBoundingClientRect().left + c.offsetWidth / 2 - (el.getBoundingClientRect().left + el.offsetWidth / 2)));
      const newIndex = centers.indexOf(Math.min(...centers));
      if (newIndex !== -1) setIndex(newIndex);
    };
    el.addEventListener("scroll", onScroll, { passive: true });
    return () => el.removeEventListener("scroll", onScroll);
  }, []);

  return (
    <div className="w-full">
      <div className="relative">
        <div
          ref={scrollerRef}
          className="flex  gap-3 overflow-x-auto scroll-smooth snap-x snap-mandatory pb-2"
          aria-roledescription="carousel"
          aria-label={`${title} image gallery`}
          id={`carousel-${id}`}
        >
          {slides.map((src, i) => (
            <div
              key={i}
              className="relative min-w-[80%] sm:min-w-[55%] md:min-w-[45%] lg:min-w-[33%] aspect-[4/3] snap-center rounded-2xl overflow-hidden bg-gray-100"
              role="group"
              aria-roledescription="slide"
              aria-label={`Slide ${i + 1} of ${slides.length}`}
            >
              {src ? (
                // eslint-disable-next-line @next/next/no-img-element
                <img src={src.url ?? src} alt={src.alt ?? `${title} photo ${i + 1}`} className="h-full w-full object-cover" />
              ) : (
                <div className="h-full w-full bg-gradient-to-br from-gray-100 to-gray-200 flex items-center justify-center">
                  <span className="text-sm text-gray-500">Add images</span>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Controls */}
        <div className="absolute inset-y-0 left-0 right-0 flex items-center justify-between pointer-events-none">
          <button
            type="button"
            onClick={() => scrollTo(Math.max(0, index - 1))}
            className="pointer-events-auto ml-2 sm:ml-3 rounded-full bg-white/80 backdrop-blur px-3 py-2 shadow hover:bg-white"
            aria-controls={`carousel-${id}`}
            aria-label="Previous slide"
          >
            ←
          </button>
          <button
            type="button"
            onClick={() => scrollTo(Math.min(slides.length - 1, index + 1))}
            className="pointer-events-auto mr-2 sm:mr-3 rounded-full bg-white/80 backdrop-blur px-3 py-2 shadow hover:bg-white"
            aria-controls={`carousel-${id}`}
            aria-label="Next slide"
          >
            →
          </button>
        </div>
      </div>

      {/* Dots */}
      <div className="mt-2 flex items-center justify-center gap-1">
        {slides.map((_, i) => (
          <button
            key={i}
            onClick={() => scrollTo(i)}
            className={classNames(
              "h-1.5 w-4 rounded-full transition-all",
              i === index ? "bg-gray-900 w-6" : "bg-gray-300"
            )}
            aria-label={`Go to slide ${i + 1}`}
          />
        ))}
      </div>
    </div>
  );
}

function OnsenCard({ item, index }) {
  return (
    <motion.article
      initial={{ opacity: 0, y: 12 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true, amount: 0.2 }}
      transition={{ duration: 0.35, delay: index * 0.04 }}
      className="rounded-3xl border border-gray-200 bg-white shadow-sm p-4 sm:p-6"
    >
      <div id="onsens" className="flex flex-col gap-4">
        <div className="flex flex-col sm:flex-row sm:items-start sm:justify-between gap-3">
          <div>
            <h3 className="text-lg sm:text-xl font-semibold tracking-tight">{item.name}</h3>
            <div className="mt-1 flex flex-wrap items-center gap-2 text-sm text-gray-600">
              <span className="inline-flex items-center gap-1">⭐ {item.rating?.toFixed?.(1) ?? item.rating}</span>
              {item.tags?.map((t) => (
                <span key={t} className="px-2 py-0.5 rounded-full bg-gray-100 text-gray-700 border border-gray-200">
                  {t}
                </span>
              ))}
              {typeof item.votes === "number" && (
                <span className="text-gray-500">• {item.votes}</span>
              )}
            </div>
          </div>
          <div className="text-sm text-gray-600 shrink-0">
            <div className="font-medium">{item.location}</div>
            {item.access && <div className="opacity-80">Access: {item.access}</div>}
          </div>
        </div>

        <Slider images={item.images} title={item.name} />

        <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 text-sm">
          <div className="space-y-1.5">
            {item.summary && <p className="text-gray-800">{item.summary}</p>}
            {item.highlight && (
              <p className="text-gray-700"><span className="font-medium">Highlight:</span> {item.highlight}</p>
            )}
          </div>
          <div className="space-y-1.5">
            {item.hours && <p className="text-gray-700"><span className="font-medium">Hours:</span> {item.hours}</p>}
            {item.admission && (
              <p className="text-gray-700"><span className="font-medium">Admission:</span> {item.admission}</p>
            )}
            {item.sources?.length > 0 && (
              <p className="text-gray-700">
                <span className="font-medium">Sources:</span>{" "}
                {item.sources.map((s, i) => (
                  <a key={i} href={s.url} target="_blank" rel="noreferrer" className="underline decoration-dotted underline-offset-2 hover:text-gray-900">
                    {s.label}
                  </a>
                )).reduce((prev, curr) => [prev, ", ", curr])}
              </p>
            )}
          </div>
        </div>
      </div>
    </motion.article>
  );
}

function ComparisonTable() {
  return (
    <div className="overflow-x-auto">
      <table className="min-w-full text-sm border-separate border-spacing-y-1">
        <thead>
          <tr className="text-left text-gray-600">
            <th className="px-3 py-2">Facility</th>
            <th className="px-3 py-2">Access from Yumoto</th>
            <th className="px-3 py-2">Price Range</th>
            <th className="px-3 py-2">Highlight</th>
          </tr>
        </thead>
        <tbody>
          {COMPARISON.map((row, i) => (
            <tr key={i} className="bg-white shadow-sm rounded-xl">
              {row.map((cell, j) => (
                <td key={j} className="px-3 py-2 border-t border-gray-100 first:rounded-l-xl last:rounded-r-xl">
                  {cell}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function Tips() {
  const tips = [
    "Arrive in the afternoon to finish your bath and still catch dinner/transport.",
    "Check last entry times and confirm the day‑use plan (some are limited hours).",
    "If you have visible tattoos, call ahead—some baths restrict them.",
    "Bring or rent towels; inclusions vary.",
    "Reserve private open‑air baths early (e.g., Hakone Yuryo’s Hanare Yuya Kaden).",
    "Choose based on your priority: view, nature setting, ease of access, budget, or luxury.",
  ];
  return (
    <ul className="list-disc pl-5 space-y-1 text-sm text-gray-700">
      {tips.map((t, i) => (
        <li key={i}>{t}</li>
      ))}
    </ul>
  );
}

export default function HakoneOnsenSection({ items = ONSEN }) {
  const titleId = useId();
  const [q, setQ] = useState("");

  const filtered = useMemo(() => {
    const query = q.trim().toLowerCase();
    if (!query) return items;
    return items.filter((x) =>
      [x.name, x.summary, x.highlight, x.location, x.access, ...(x.tags || [])]
        .filter(Boolean)
        .join(" ")
        .toLowerCase()
        .includes(query)
    );
  }, [items, q]);

  return (
    <section className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8 py-10">
      <div className="flex flex-col gap-6">
        <header className="flex flex-col md:flex-row md:items-end md:justify-between gap-4">
          <div>
            <h2 id={titleId} className="text-2xl md:text-3xl font-bold tracking-tight">
              Hakone Day‑Use Onsen Picks
            </h2>
            <p className="mt-1 text-gray-600 max-w-2xl">
              A curated list of baths we want to visit—each with a mini image gallery. Swap in your own photos and links when ready.
            </p>
          </div>

          <div className="flex items-center gap-2 w-full md:w-80">
            <input
              type="search"
              value={q}
              onChange={(e) => setQ(e.target.value)}
              placeholder="Search: name, tag, access, etc."
              className="w-full rounded-2xl border border-gray-300 px-3 py-2 text-sm shadow-sm focus:outline-none focus:ring-2 focus:ring-gray-800"
            />
            <button
              onClick={() => setQ("")}
              className="shrink-0 rounded-2xl border border-gray-300 px-3 py-2 text-sm bg-white hover:bg-gray-50"
            >
              Clear
            </button>
          </div>
        </header>

        <div className="grid grid-cols-1 gap-6">
          {filtered.map((item, idx) => (
            <OnsenCard key={item.id} item={item} index={idx} />
          ))}
        </div>

        <div className="mt-8">
          <h3 className="text-xl font-semibold">Quick Comparison</h3>
          <p className="text-sm text-gray-600 mb-3">Fast overview of access, price, and highlights.</p>
          <ComparisonTable />
        </div>

        <div className="mt-6">
          <h3 className="text-xl font-semibold">Tips for Day‑Use Onsen</h3>
          <Tips />
        </div>
      </div>
    </section>
  );
}
